import { createStore } from 'vuex'

export default createStore({
  state: {
    products: [],
  },
  getters: {
    getProducts() {
      return this.state.products;
    }
  },
  mutations: {
    async fetchProducts() {
      const product = await fetch('https://fakestoreapi.com/products/1');
      console.log(product);
    }
  },
  actions: {
    fetchProducts({ commit }) {
      commit('fetchProducts');
    }
  }
})
